mapper.update:
update address
set phone = #{phone},
zipcode = #{zipcode}, 
address1 = #{address1}, 
address2 = #{address2} 
where no = #{no}

mapper.select:
select no,name,phone,to_char(wdate,'yyyy-dd-mm') as wdate ,r
from(select no,name,phone,wdate ,rownum r                  
	from(select no,name,phone,wdate from address
	
	
	name like '%'||#{word}||'%'
	
	
	phone like '%'||#{word}||'%'
	
	
	order by no desc
	)
)

where r>=#{sno} and r<=#{eno}
